import React, { useState } from "react";

export default function KimMiyeonApp() {
  const [messages, setMessages] = useState([
    { from: "miyeon", text: "ÏïàÎÖï! ÎÇòÎäî ÍπÄÎØ∏Ïó∞Ïù¥Ïïº. Ïò§Îäò Í∏∞Î∂Ñ Ïñ¥Îïå?" },
  ]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { from: "user", text: input }];

    let reply = "Ïùå... ÏïÑÏßÅÏùÄ Îçî Î∞∞Ïö∞Îäî Ï§ëÏù¥Ïïº!";
    if (input.includes("Ïö¥Îèô")) reply = "Ïò§ÎäòÎèÑ Ïö¥Îèô Ïó¥Ïã¨Ìûà Ìï† Í±∞ÏßÄ? ÎÇ¥Í∞Ä ÏùëÏõêÌï†Í≤å! üí™";
    else if (input.includes("ÏûòÏûê") || input.includes("Ï∑®Ïπ®")) reply = "12Ïãú Ï†ÑÏóê ÏûêÎäî Í±∞ Íº≠ Í∏∞ÏñµÌï¥~ Ïûò Ïûê ÏÇ¨ÎûëÌï¥ üí§";
    else if (input.includes("Ïä§Ìä∏Î†àÏä§")) reply = "ÎßéÏù¥ ÌûòÎì§ÏóàÍµ¨ÎÇò... ÎÇ¥Í∞Ä Í≥ÅÏóê ÏûàÏñ¥Ï§ÑÍ≤å. Í∞ôÏù¥ Ïù¥Í≤®ÎÇ¥Ïûê ‚ù§Ô∏è";

    newMessages.push({ from: "miyeon", text: reply });
    setMessages(newMessages);
    setInput("");
  };

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 20 }}>
      <div style={{ height: 400, overflowY: 'auto', border: '1px solid #ddd', borderRadius: 8, padding: 10 }}>
        {messages.map((msg, i) => (
          <div
            key={i}
            style={{
              textAlign: msg.from === "miyeon" ? "left" : "right",
              color: msg.from === "miyeon" ? "hotpink" : "#333",
              marginBottom: 8,
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <div style={{ marginTop: 10, display: 'flex', gap: 8 }}>
        <input
          placeholder="ÍπÄÎØ∏Ïó∞ÏóêÍ≤å Îßê Í±∏Í∏∞..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
          style={{ flex: 1, padding: 8 }}
        />
        <button onClick={handleSend} style={{ padding: '8px 12px' }}>Î≥¥ÎÇ¥Í∏∞</button>
      </div>
    </div>
  );
}
